mapper.select:
SELECT h.extend_field_id, h.content_id, h.rule_id, h.field_name, h.field_value FROM crawler_extend_field h WHERE 1 = 1
AND h.content_id = #{contentId}
AND h.rule_id = #{ruleId}
AND h.field_name = #{fieldName}
AND h.field_value = #{fieldValue}
ORDER BY h.extend_field_id DESC

    SELECT count(*)
    FROM 
          crawler_extend_field h
    WHERE 1 = 1

	        AND h.content_id = #{contentId}  


	        AND h.rule_id = #{ruleId}  


	        AND h.field_name = #{fieldName}  


	        AND h.field_value = #{fieldValue}  

  	ORDER BY 
          h.extend_field_id DESC

mapper.update:
    UPDATE crawler_extend_field 


		            content_id = #{contentId}, 


		            rule_id = #{ruleId}, 


		            field_name = #{fieldName}


		            field_value = #{fieldValue}


    WHERE extend_field_id = #{extendFieldId}
