mapper.select:
    SELECT   
          r.resource_id,
     	  r.content_id,
          r.rule_id,
          r.path,
          r.name,
          r.type,
          r.has_upload,
          r.has_dealWith,
          r.is_local
    FROM 
          crawler_content_resource r
    WHERE 1 = 1

	        AND r.rule_id = #{ruleId}  


	        AND r.content_id = #{contentId}  


	        AND r.has_upload = #{hasUpload}  


	        AND r.has_dealWith = #{hasDealWith}  


	        AND r.is_local = #{isLocal}  


	        AND r.resource_id IN

		    	#{resourceIdList}



	        AND r.content_id IN

		    	#{contentIdList}



	        AND r.rule_id IN

		    	#{ruleIdList}


  	ORDER BY 
          r.resource_id DESC

    SELECT count(*)
    FROM 
          crawler_content_resource r
    WHERE 1 = 1

	        AND r.rule_id = #{ruleId}  


	        AND r.content_id = #{contentId}  


	        AND r.has_upload = #{hasUpload}  


	        AND r.has_dealWith = #{hasDealWith}  


	        AND r.is_local = #{isLocal}  


	        AND r.resource_id IN

		    	#{resourceIdList}



	        AND r.content_id IN

		    	#{contentIdList}



	        AND r.rule_id IN

		    	#{ruleIdList}


  	ORDER BY 
          r.resource_id DESC
