mapper.select:
SELECT r.rule_id, r.rule_name, r.start_time, r.end_time, r.status, r.total_item, r.rule_base_config, r.rule_content_config, r.rule_content_page_config, r.rule_comment_config, r.rule_fields_config, r.rule_database_config, r.rule_ftp_config, r.rule_ImageSetting_config, r.rule_local_config, r.rule_type FROM crawler_rule r WHERE r.rule_type = #{ruleType}
AND r.status = #{status}
ORDER BY r.rule_id DESC

SELECT count(*) FROM crawler_rule r WHERE r.rule_type = #{ruleType}
AND r.status = #{status}
ORDER BY r.rule_id DESC

mapper.update:
    UPDATE crawler_rule 


		            status = #{status}, 


		            start_time = #{startTime}


    WHERE  rule_id IN 

    	#{ruleIdList}

    UPDATE crawler_rule 


		            status = #{status}, 


		            total_item = #{totalItem}, 


		            end_time = #{endTime}


     WHERE rule_id = #{ruleId}
